or j=1:1:coluna3    if med(i,j)==1      sum3++;    endif    endendmedia1=sum3/14  % CÁLCULO ÁREA MÉDIA DE UM OVO MÉT 2sum4=0;for i=1:1:linha4  for j=1:1:coluna4    if med2(i,j)==1      sum4++;    endif    endendmedia2=sum4/14num_ovos1=sum1/media1num_ovos2=sum2/media2% APLICAÇÃO DO WATERSHED##% Calcular bwdist(), Computar segmentação watershed() ##  % Calculo Euclidiano bwdist##Dimg = bwdist(~imgCl);##  ##  % Aplicação da segmentacao por watershed##Dimg = -Dimg;##L = watershed(Dimg);####  % Segmentos aplicados à imagem binaria tratada##L(~Dimg) = 0;##  ##  % Aplicando cor para melhor visualização##rgb = label2rgb(L,'jet',[.5 .5 .5]);##figure,##imshow(rgb);####imwrite(rgb,'WatSh_cells.png');##title('Transformada Watershed');